import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaGithub, FaLinkedin, FaEnvelope, FaPhone } from 'react-icons/fa';
import { theme } from '../utils/theme';
import { device } from '../utils/mediaQueries';  // Add this import

const ContactSection = styled.section`
  min-height: 100vh;
  padding: 100px 0;
  background: linear-gradient(135deg, ${theme.background} 0%, ${theme.lightGray} 100%);
  color: ${theme.primary};
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const Title = styled.h2`
  text-align: center;
  font-size: 2.5rem;
  margin-bottom: 3rem;
  color: ${theme.accent};
`;

const ContactGrid = styled.div`
  grid-template-columns: 1fr;
  gap: 2rem;
  
  ${device.tablet} {
    grid-template-columns: repeat(2, 1fr);
    gap: 3rem;
  }
`;

const FormInput = styled.input`
  padding: 0.8rem;
  font-size: 0.9rem;
  background: ${theme.background};
  border: 1px solid ${theme.darkGray};
  color: ${theme.primary};
  
  ${device.mobile} {
    padding: 0.9rem;
    font-size: 1rem;
  }
  
  ${device.tablet} {
    padding: 1rem;
    font-size: 1.1rem;
  }
`;

// Update the component usage to use FormInput instead of Input
const TextArea = styled.textarea`
  padding: 0.8rem;
  font-size: 0.9rem;
  
  ${device.mobile} {
    padding: 0.9rem;
    font-size: 1rem;
  }
  
  ${device.tablet} {
    padding: 1rem;
    font-size: 1.1rem;
  }
`;

const ContactCard = styled(motion.div)`
  padding: 1rem;
  
  ${device.mobile} {
    padding: 1.2rem;
  }
  
  ${device.tablet} {
    padding: 1.5rem;
  }
`;

const ContactForm = styled(motion.form)`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const Input = styled.input`
  padding: 1rem;
  background: ${theme.background};
  border: 1px solid ${theme.darkGray};
  border-radius: 5px;
  color: ${theme.primary};
  font-size: 1rem;

  &:focus {
    outline: 2px solid ${theme.accent};
  }
`;

const FormTextArea = styled.textarea`
  padding: 1rem;
  background: ${theme.background};
  border: 1px solid ${theme.darkGray};
  border-radius: 5px;
  color: ${theme.primary};
  font-size: 1rem;
  min-height: 150px;

  &:focus {
    outline: 2px solid ${theme.accent};
  }
`;

// Update usage in the component to use FormTextArea instead of TextArea
const SubmitButton = styled(motion.button)`
  padding: 1rem 2rem;
  background: ${theme.accent};
  border: none;
  border-radius: 5px;
  color: ${theme.background};
  font-size: 1.1rem;
  cursor: pointer;
  font-weight: bold;
`;

const ContactInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const ContactInfoCard = styled(motion.div)`
  display: flex;
  align-items: center;
  gap: 1rem;
  padding: 1rem;
  background: ${theme.background};
  border-radius: 10px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
  
  ${device.mobile} {
    padding: 1.2rem;
  }
  
  ${device.tablet} {
    padding: 1.5rem;
  }
`;

// Update the usage in your JSX from ContactCard to ContactInfoCard
const Contact = () => {
  return (
    <ContactSection id="contact">
      <Container>
        <Title>Contact Me</Title>
        <ContactGrid>
          <ContactForm
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true }}
          >
            <FormInput type="text" placeholder="Your Name" />
            <FormInput type="email" placeholder="Your Email" />
            <FormTextArea placeholder="Your Message" />
            <SubmitButton
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Send Message
            </SubmitButton>
          </ContactForm>

          <ContactInfo>
            {[
              { icon: <FaEnvelope />, text: "gokulkannangk28@gmail.com" },
              { icon: <FaPhone />, text: "+91 9361443302" },
              { icon: <FaGithub />, text: "https://github.com/" },
              { icon: <FaLinkedin />, text: "https://www.linkedin.com/feed/" }
            ].map((item, index) => (
              <ContactInfoCard
                key={index}
                initial={{ opacity: 0, x: 50 }}
                whileInView={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                viewport={{ once: true }}
                whileHover={{ scale: 1.05 }}
              >
                {item.icon}
                <span>{item.text}</span>
              </ContactInfoCard>
            ))}
          </ContactInfo>
        </ContactGrid>
      </Container>
    </ContactSection>
  );
};

export default Contact;
