import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { theme } from '../utils/theme';
import { device } from '../utils/mediaQueries';
import { FaDownload } from 'react-icons/fa';

const HeroSection = styled.section`
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: ${theme.background};
  padding: 0 20px;
  position: relative;
  overflow: hidden;
`;

const Content = styled.div`
  text-align: left;
  color: ${theme.text};
  max-width: 1000px;
  width: 100%;
  z-index: 2;
  padding: 0 10px;

  ${device.tablet} {
    padding: 0 50px;
  }
`;

const PreTitle = styled(motion.p)`
  color: ${theme.accent};
  font-family: ${theme.fontMono};
  font-size: 0.9rem;
  margin-bottom: 1rem;

  ${device.tablet} {
    font-size: 1rem;
    margin-bottom: 1.5rem;
  }
`;

const Title = styled(motion.h1)`
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: ${theme.lightGray};
  font-weight: 600;
  line-height: 1.1;

  ${device.mobile} {
    font-size: 3rem;
  }

  ${device.tablet} {
    font-size: 4.5rem;
  }

  ${device.laptop} {
    font-size: 5rem;
  }
`;

const Subtitle = styled(motion.h2)`
  font-size: 2rem;
  color: ${theme.secondary};
  margin-bottom: 1.5rem;
  font-weight: 600;
  line-height: 1.1;

  ${device.tablet} {
    font-size: 4rem;
    margin-bottom: 2rem;
  }
`;

const Description = styled(motion.p)`
  font-size: 0.9rem;
  color: ${theme.text};
  max-width: 540px;
  margin-bottom: 2rem;
  line-height: 1.6;

  ${device.tablet} {
    font-size: 1.1rem;
    margin-bottom: 3rem;
  }
`;

const DownloadButton = styled(motion.a)`
  display: inline-flex;
  align-items: center;
  gap: 10px;
  padding: 1rem 1.5rem;
  background: transparent;
  color: ${theme.accent};
  text-decoration: none;
  border: 1px solid ${theme.accent};
  border-radius: ${theme.borderRadius};
  font-family: ${theme.fontMono};
  font-size: 0.9rem;
  transition: all 0.25s cubic-bezier(0.645, 0.045, 0.355, 1);

  ${device.tablet} {
    padding: 1.25rem 1.75rem;
  }

  &:hover {
    background: rgba(100, 255, 218, 0.1);
    transform: translateY(-3px);
  }

  svg {
    font-size: 1.1rem;
  }
`;

const Hero = () => {
  return (
    <HeroSection id="home">
      <Content>
        <PreTitle
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.5 }}
        >
          Hi, my name is
        </PreTitle>
        <Title
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.6 }}
        >
          Gokulakannan S.
        </Title>
        <Subtitle
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.7 }}
        >
          I build things for the web.
        </Subtitle>
        <Description
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.8 }}
        >
          I'm a B.Tech (Information Technology) student specializing in building (and occasionally designing) exceptional digital experiences. Currently, I'm focused on building accessible, human-centered products.
        </Description>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.9 }}
        >
          <DownloadButton
            href="/files/resume.pdf"
            download="Gokul_CV.pdf"
            target="_blank"
            rel="noopener noreferrer"
          >
            <FaDownload /> Download Resume
          </DownloadButton>
        </motion.div>
      </Content>
    </HeroSection>
  );
};

export default Hero;
