import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { theme } from '../utils/theme';
import { device } from '../utils/mediaQueries';  // Fix the import path

const EducationSection = styled.section`
  min-height: 100vh;
  padding: 100px 0;
  background: ${theme.background};
  color: ${theme.primary};
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;

  ${device.tablet} {
    padding: 40px 20px;
  }
`;

const Timeline = styled.div`
  position: relative;
  max-width: 600px;
  margin: 0 auto;

  &::after {
    content: '';
    position: absolute;
    width: 2px;
    background: ${theme.accent};
    top: 0;
    bottom: 0;
    left: 50%;
    margin-left: -1px;
  }
  
  ${device.tablet} {
    max-width: 800px;
  }
`;

const TimelineContent = styled.div`
  background: ${theme.background};
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);

  h3 {
    color: ${theme.accent};
    margin-bottom: 10px;
    font-size: 1.1rem;
    
    ${device.mobile} {
      font-size: 1.2rem;
    }
    
    ${device.tablet} {
      font-size: 1.3rem;
    }
  }

  p {
    color: ${theme.secondary};
    margin: 5px 0;
    font-size: 0.9rem;
    
    ${device.mobile} {
      font-size: 1rem;
    }
    
    ${device.tablet} {
      font-size: 1.1rem;
    }
  }
`;
const Title = styled.h2`
  text-align: center;
  font-size: 2.5rem;
  margin-bottom: 3rem;
  color: ${theme.accent};
`;

const TimelineItem = styled(motion.div)`
  padding: 10px 40px;
  position: relative;
  width: 50%;
  left: ${props => props.align === 'left' ? '0' : '50%'};
  
  &::after {
    content: '';
    position: absolute;
    width: 20px;
    height: 20px;
    right: ${props => props.align === 'left' ? '-10px' : 'auto'};
    left: ${props => props.align === 'left' ? 'auto' : '-10px'};
    background: ${theme.accent};
    border-radius: 50%;
    top: 15px;
    transition: all 0.3s ease;
  }

  &:hover::after {
    transform: scale(1.5);
    box-shadow: 0 0 15px ${theme.accent}80;
  }
`;

const Education = () => {
  return (
    <EducationSection id="education">
      <Container>
        <Title data-aos="fade-down">Education</Title>
        <Timeline>
          <TimelineItem
            align="left"
            data-aos="fade-right"
          >
            <TimelineContent>
              <h3>B.Tech(INFORMATION TECHNOLOGY)</h3>
              <p>ANNA UNIVERSITY</p>
              <p>2022 - 2026</p>
            </TimelineContent>
          </TimelineItem>
          <TimelineItem
            align="right"
            data-aos="fade-left"
          >
            <TimelineContent>
              <h3>Higher Secondary School</h3>
              <p>Kurinchi Matric Higher Secondary School</p>
              <p>2020 - 2022</p>
            </TimelineContent>
          </TimelineItem>
        </Timeline>
      </Container>
    </EducationSection>
  );
};

export default Education;
