import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { theme } from '../utils/theme';

const AboutSection = styled.section`
  min-height: 100vh;
  padding: 100px 0;
  background: ${theme.background};
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: linear-gradient(45deg, rgba(0, 102, 204, 0.1) 0%, transparent 100%);
    pointer-events: none;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 50px;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    text-align: center;
  }
`;

const ImageContainer = styled(motion.div)`
  width: 300px;
  height: 300px;
  border-radius: 50%;
  overflow: hidden;
  border: 5px solid ${theme.accent};
  box-shadow: 0 0 20px ${theme.accent}4D;
  margin: 0 auto;
`;

const ProfileImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const Content = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;

  h2 {
    font-size: 2.5rem;
    margin-bottom: 1rem;
    color: ${theme.primary};
  }

  p {
    font-size: 1.1rem;
    line-height: 1.8;
    color: ${theme.secondary};
  }
`;

const About = () => {
  return (
    <AboutSection id="about">
      <Container>
        <ImageContainer data-aos="zoom-in">
          <ProfileImage src="/your-photo.jpg" alt="Profile" />
        </ImageContainer>
        <Content>
          <motion.h2 data-aos="fade-up">About Me</motion.h2>
          <motion.p data-aos="fade-up" data-aos-delay="200">
            Your about me text goes here...
          </motion.p>
        </Content>
      </Container>
    </AboutSection>
  );
};

export default About;
