import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const EducationSection = styled.section`
  min-height: 100vh;
  padding: 100px 0;
  background: #1e1e1e;
  color: white;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const Title = styled.h2`
  text-align: center;
  font-size: 2.5rem;
  margin-bottom: 3rem;
  color: #00f7ff;
`;

const Timeline = styled.div`
  position: relative;
  max-width: 800px;
  margin: 0 auto;

  &::after {
    content: '';
    position: absolute;
    width: 2px;
    background: #00f7ff;
    top: 0;
    bottom: 0;
    left: 50%;
    margin-left: -1px;
  }
`;

const TimelineItem = styled(motion.div)`
  padding: 10px 40px;
  position: relative;
  width: 50%;
  left: ${props => props.align === 'left' ? '0' : '50%'};
  
  &::after {
    content: '';
    position: absolute;
    width: 20px;
    height: 20px;
    right: ${props => props.align === 'left' ? '-10px' : 'auto'};
    left: ${props => props.align === 'left' ? 'auto' : '-10px'};
    background: #00f7ff;
    border-radius: 50%;
    top: 15px;
    transition: all 0.3s ease;
  }

  &:hover::after {
    transform: scale(1.5);
    box-shadow: 0 0 15px rgba(0, 247, 255, 0.5);
  }
`;

const TimelineContent = styled.div`
  padding: 20px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 10px;
  backdrop-filter: blur(5px);

  h3 {
    margin-bottom: 10px;
    color: #00f7ff;
  }

  p {
    margin: 0;
    line-height: 1.6;
  }
`;

const Education = () => {
  return (
    <EducationSection id="education">
      <Container>
        <Title data-aos="fade-down">Education</Title>
        <Timeline>
          <TimelineItem
            align="left"
            data-aos="fade-right"
          >
            <TimelineContent>
              <h3>Bachelor of Computer Science</h3>
              <p>Your University Name</p>
              <p>2020 - Present</p>
            </TimelineContent>
          </TimelineItem>
          <TimelineItem
            align="right"
            data-aos="fade-left"
          >
            <TimelineContent>
              <h3>High School Diploma</h3>
              <p>Your High School Name</p>
              <p>2016 - 2020</p>
            </TimelineContent>
          </TimelineItem>
        </Timeline>
      </Container>
    </EducationSection>
  );
};

export default Education;
