import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { theme } from '../utils/theme';
import { device } from '../utils/mediaQueries';

const HeroSection = styled.section`
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(135deg, ${theme.background} 0%, ${theme.lightGray} 100%);
  padding: 0 20px;
`;

const Content = styled.div`
  text-align: center;
  color: ${theme.primary};
  max-width: 800px;
  width: 100%;
`;

const Title = styled(motion.h1)`
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: ${theme.primary};

  ${device.mobile} {
    font-size: 3rem;
  }

  ${device.tablet} {
    font-size: 3.5rem;
  }

  ${device.laptop} {
    font-size: 4rem;
  }

  ${device.desktop} {
    font-size: 4.5rem;
  }
`;

const Subtitle = styled(motion.p)`
  font-size: 1rem;
  color: ${theme.accent};

  ${device.mobile} {
    font-size: 1.2rem;
  }

  ${device.tablet} {
    font-size: 1.3rem;
  }

  ${device.laptop} {
    font-size: 1.5rem;
  }

  ${device.desktop} {
    font-size: 1.6rem;
  }
`;

const Hero = () => {
  return (
    <HeroSection id="home">
      <Content>
        <Title
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          GOKULAKANNAN S
        </Title>
        <Subtitle
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          B.Tech(INFORMATION TECHNOLOGY)
        </Subtitle>
      </Content>
    </HeroSection>
  );
};

export default Hero;
