import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { theme } from '../utils/theme';
import { device } from '../utils/mediaQueries';
import { FaDownload } from 'react-icons/fa';

const HeroSection = styled.section`
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(135deg, ${theme.background} 0%, ${theme.lightGray} 100%);
  padding: 0 20px;
  position: relative;
  overflow: hidden;
`;

const Content = styled.div`
  text-align: center;
  color: ${theme.primary};
  max-width: 800px;
  width: 100%;
  z-index: 2;
`;

const Title = styled(motion.h1)`
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: ${theme.primary};
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
  background: linear-gradient(to right, ${theme.primary}, ${theme.accent});
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;

  ${device.mobile} {
    font-size: 3rem;
  }

  ${device.tablet} {
    font-size: 3.5rem;
  }

  ${device.laptop} {
    font-size: 4rem;
  }

  ${device.desktop} {
    font-size: 4.5rem;
  }
`;

const Subtitle = styled(motion.p)`
  font-size: 1rem;
  color: ${theme.accent};
  margin-bottom: 2rem;

  ${device.mobile} {
    font-size: 1.2rem;
  }

  ${device.tablet} {
    font-size: 1.3rem;
  }

  ${device.laptop} {
    font-size: 1.5rem;
  }

  ${device.desktop} {
    font-size: 1.6rem;
  }
`;

const DownloadButton = styled(motion.a)`
  display: inline-flex;
  align-items: center;
  gap: 10px;
  padding: 1rem 2rem;
  background: ${theme.accent};
  color: white;
  text-decoration: none;
  border-radius: 30px;
  font-weight: bold;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 6px 20px rgba(0, 0, 0, 0.25);
  }

  svg {
    font-size: 1.2rem;
  }
`;

const BackgroundAnimation = styled(motion.div)`
  position: absolute;
  width: 60vw;
  height: 60vw;
  background: radial-gradient(circle, ${theme.accent}20 0%, transparent 70%);
  border-radius: 50%;
  z-index: 1;
`;

const Hero = () => {
  return (
    <HeroSection id="home">
      <BackgroundAnimation
        animate={{
          scale: [1, 1.2, 1],
          opacity: [0.3, 0.5, 0.3],
        }}
        transition={{
          duration: 8,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
      <Content>
        <Title
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          GOKULAKANNAN S
        </Title>
        <Subtitle
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          B.Tech(INFORMATION TECHNOLOGY)
        </Subtitle>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 1 }}
        >
          <DownloadButton
            href="/files/Gokul_CV.pdf"
            download="Gokul_CV.pdf"
            target="_blank"
            rel="noopener noreferrer"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <FaDownload /> Download CV
          </DownloadButton>
        </motion.div>
      </Content>
    </HeroSection>
  );
};

export default Hero;
