import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FaArrowUp } from 'react-icons/fa';

const ScrollButton = styled.button`
  position: fixed;
  bottom: 40px;
  right: 40px;
  background: linear-gradient(145deg, #00f7ff, #00d8ff);
  width: 50px;
  height: 50px;
  border-radius: 50%;
  border: none;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: ${props => props.visible ? '1' : '0'};
  transform: ${props => props.visible ? 'translateY(0)' : 'translateY(100px)'};
  transition: all 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  z-index: 1000;
  box-shadow: 0 4px 15px rgba(0, 247, 255, 0.2);

  &:hover {
    transform: ${props => props.visible ? 'translateY(-5px)' : 'translateY(100px)'};
    box-shadow: 0 8px 25px rgba(0, 247, 255, 0.4);
  }

  &:active {
    transform: translateY(0);
    transition: all 0.1s ease;
  }

  svg {
    transition: transform 0.3s ease;
  }

  &:hover svg {
    transform: scale(1.2);
  }
`;

const ScrollToTop = () => {
  const [visible, setVisible] = useState(false);
  const [scrollDirection, setScrollDirection] = useState('up');
  const [lastScroll, setLastScroll] = useState(0);

  useEffect(() => {
    const toggleVisible = () => {
      const currentScroll = window.pageYOffset;
      setVisible(currentScroll > 300);
      
      if (currentScroll > lastScroll) {
        setScrollDirection('down');
      } else {
        setScrollDirection('up');
      }
      setLastScroll(currentScroll);
    };

    window.addEventListener('scroll', toggleVisible);
    return () => window.removeEventListener('scroll', toggleVisible);
  }, [lastScroll]);

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
      duration: 1000
    });
  };

  return (
    <ScrollButton 
      visible={visible} 
      onClick={scrollToTop}
      style={{
        transform: `${visible ? 'translateY(0)' : 'translateY(100px)'} 
                   ${scrollDirection === 'down' ? 'rotate(180deg)' : 'rotate(0)'}`
      }}
    >
      <FaArrowUp color="#1e1e1e" />
    </ScrollButton>
  );
};

export default ScrollToTop;
