import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { theme } from '../utils/theme';
import { device } from '../utils/mediaQueries';
import { FaCertificate, FaExternalLinkAlt } from 'react-icons/fa';

const CertificationsSection = styled.section`
  padding: 100px 0;
  background: ${theme.lightGray};
  color: ${theme.primary};
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const Title = styled.h2`
  text-align: center;
  font-size: 2.5rem;
  margin-bottom: 3rem;
  color: ${theme.accent};
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 4px;
    background: ${theme.accent};
    border-radius: 2px;
  }
`;

const CertGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: 2rem;

  ${device.tablet} {
    grid-template-columns: repeat(2, 1fr);
  }

  ${device.laptop} {
    grid-template-columns: repeat(3, 1fr);
  }
`;

const CertCard = styled(motion.div)`
  background: ${theme.background};
  padding: 2rem;
  border-radius: 15px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  border: 1px solid rgba(0, 0, 0, 0.05);

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.15);
    border-color: ${theme.accent};
  }
`;

const IconWrapper = styled.div`
  font-size: 3rem;
  color: ${theme.accent};
  margin-bottom: 1.5rem;
`;

const CertName = styled.h3`
  font-size: 1.4rem;
  margin-bottom: 0.5rem;
  color: ${theme.primary};
`;

const Issuer = styled.p`
  font-size: 1rem;
  color: ${theme.secondary};
  margin-bottom: 1rem;
  font-weight: 500;
`;

const Date = styled.span`
  font-size: 0.9rem;
  color: ${theme.darkGray};
  margin-bottom: 1.5rem;
  display: block;
`;

const CredentialLink = styled.a`
  display: inline-flex;
  align-items: center;
  gap: 8px;
  color: ${theme.accent};
  text-decoration: none;
  font-weight: 600;
  transition: color 0.2s;

  &:hover {
    color: ${theme.primary};
  }
`;

const Certifications = () => {
  const certifications = [
    {
      name: "AWS Certified Cloud Practitioner",
      issuer: "Amazon Web Services",
      date: "Issued Jan 2024",
      link: "#"
    },
    {
      name: "Meta Front-End Developer",
      issuer: "Meta",
      date: "Issued Dec 2023",
      link: "#"
    },
    {
      name: "Google Data Analytics",
      issuer: "Google",
      date: "Issued Nov 2023",
      link: "#"
    }
  ];

  return (
    <CertificationsSection id="certifications">
      <Container>
        <Title data-aos="fade-down">Certifications</Title>
        <CertGrid>
          {certifications.map((cert, index) => (
            <CertCard
              key={index}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              viewport={{ once: true }}
            >
              <IconWrapper>
                <FaCertificate />
              </IconWrapper>
              <CertName>{cert.name}</CertName>
              <Issuer>{cert.issuer}</Issuer>
              <Date>{cert.date}</Date>
              <CredentialLink href={cert.link} target="_blank" rel="noopener noreferrer">
                View Credential <FaExternalLinkAlt size={12} />
              </CredentialLink>
            </CertCard>
          ))}
        </CertGrid>
      </Container>
    </CertificationsSection>
  );
};

export default Certifications;
