import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { theme } from '../utils/theme';
import { device } from '../utils/mediaQueries';

const AboutSection = styled.section`
  min-height: 100vh;
  padding: 100px 0;
  background: ${theme.lightGray};
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: linear-gradient(45deg, ${theme.accent}1A 0%, transparent 70%);
    pointer-events: none;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  display: grid;
  grid-template-columns: 1fr;
  gap: 30px;
  background: ${theme.background};
  border-radius: 20px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);
  text-align: center;

  ${device.tablet} {
    grid-template-columns: 1fr 1fr;
    padding: 40px 20px;
    gap: 50px;
    text-align: left;
  }
`;

const ImageContainer = styled(motion.div)`
  width: 300px;
  height: 300px;
  border-radius: 50%;
  overflow: hidden;
  border: 5px solid ${theme.accent};
  box-shadow: 0 0 30px ${theme.accent}33;
  margin: 0 auto;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const Content = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 20px;
  background: ${theme.background};
  border-radius: 20px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);

  h2 {
    font-size: 2.5rem;
    margin-bottom: 1.5rem;
    color: ${theme.accent};
    position: relative;
    display: inline-block;

    &::after {
      content: '';
      position: absolute;
      bottom: -8px;
      left: 0;
      width: 60px;
      height: 3px;
      background: ${theme.accent};
      border-radius: 2px;
    }
  }

  p {
    font-size: 1.1rem;
    line-height: 1.8;
    color: ${theme.secondary};
    margin-bottom: 1rem;
  }
`;

const About = () => {
  return (
    <AboutSection id="about">
      <Container>
        <ImageContainer data-aos="zoom-in">
          <img 
            src="/images/profile.jpg"
            alt="Profile" 
          />
        </ImageContainer>
        <Content>
          <motion.h2 data-aos="fade-up">About Me</motion.h2>
          <motion.p data-aos="fade-up" data-aos-delay="200">
            Hi there! I'm a passionate web developer with expertise in React.js, JavaScript, and modern web technologies. I love creating responsive and user-friendly applications that solve real-world problems. With a strong foundation in front-end development and a keen eye for design, I strive to deliver high-quality solutions that exceed expectations.
          </motion.p>
        </Content>
      </Container>
    </AboutSection>
  );
};

export default About;
