import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-scroll';
import { motion, AnimatePresence } from 'framer-motion';
import { device } from '../utils/mediaQueries';
import { theme } from '../utils/theme';

const Nav = styled(motion.nav)`
  position: fixed;
  top: 0;
  width: 100%;
  height: 70px;
  background: ${props => props.scrolled ? 'rgba(255, 255, 255, 0.95)' : 'transparent'};
  backdrop-filter: ${props => props.scrolled ? 'blur(10px)' : 'none'};
  box-shadow: ${props => props.scrolled ? '0 4px 20px rgba(0, 0, 0, 0.08)' : 'none'};
  display: flex;
  justify-content: flex-start;  // Change from space-between to flex-start
  align-items: center;
  padding: 0 30px;
  z-index: 1000;
  transition: all 0.4s ease;
`;

const Logo = styled.div`
  font-size: 1.8rem;
  font-weight: bold;
  color: ${theme.accent};
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
  
  &:hover {
    transform: scale(1.05);
    transition: transform 0.3s ease;
  }
`;

const NavMenu = styled.div`
  display: none;

  ${device.tablet} {
    display: flex;
    gap: 2rem;
    align-items: center;
    margin-right: auto;  // This will push the menu to the left
    margin-left: 50px;   // Add some space from the logo
  }
`;

// Remove the duplicate Nav component (lines 48-62)

const NavLink = styled(Link)`
  font-size: 1.1rem;
  font-weight: 500;
  color: ${theme.primary};
  padding: 0.5rem 1rem;
  border-radius: 8px;
  transition: all 0.3s ease;
  
  &:hover {
    color: ${theme.accent};
    background: rgba(0, 247, 255, 0.1);
    transform: translateY(-2px);
  }

  &.active {
    color: ${theme.accent};
    background: rgba(0, 247, 255, 0.15);
  }
`;

const MobileMenu = styled(motion.div)`
  display: flex;
  flex-direction: column;
  position: fixed;
  top: 70px;
  left: 0;
  right: 0;
  padding: 1.5rem;
  gap: 1.2rem;
  background: rgba(255, 255, 255, 0.98);
  backdrop-filter: blur(10px);
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  border-bottom-left-radius: 20px;
  border-bottom-right-radius: 20px;
`;

const MenuButton = styled.button`
  font-size: 1.5rem;
  background: none;
  border: none;
  color: ${theme.primary};
  cursor: pointer;
  padding: 0.5rem;
  border-radius: 8px;
  transition: all 0.3s ease;

  &:hover {
    color: ${theme.accent};
    background: rgba(0, 247, 255, 0.1);
  }

  ${device.tablet} {
    display: none;
  }
`;

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const menuItems = [
    { title: 'Home', to: 'home' },
    { title: 'About', to: 'about' },
    { title: 'Education', to: 'education' },
    { title: 'Skills', to: 'skills' },
    { title: 'Contact', to: 'contact' }
  ];

  return (
    <Nav
      scrolled={scrolled}
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <Logo>⚡Gokul</Logo>
      
      <NavMenu>
        {menuItems.map((item) => (
          <NavLink
            key={item.to}
            to={item.to}
            smooth={true}
            duration={500}
            spy={true}
            activeStyle={{ color: '#00f7ff' }}
          >
            {item.title}
          </NavLink>
        ))}
      </NavMenu>

      <MenuButton onClick={() => setMobileMenuOpen(!mobileMenuOpen)}>
        {mobileMenuOpen ? '✕' : '☰'}
      </MenuButton>

      <AnimatePresence>
        {mobileMenuOpen && (
          <MobileMenu
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.3 }}
          >
            {menuItems.map((item) => (
              <NavLink
                key={item.to}
                to={item.to}
                smooth={true}
                duration={500}
                spy={true}
                onClick={() => setMobileMenuOpen(false)}
              >
                {item.title}
              </NavLink>
            ))}
          </MobileMenu>
        )}
      </AnimatePresence>
    </Nav>
  );
};

export default Navbar;
