import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { theme } from '../utils/theme';
import { device } from '../utils/mediaQueries';

const ProjectsSection = styled.section`
  min-height: 100vh;
  padding: 100px 0;
  background: ${theme.background};
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const Title = styled.h2`
  text-align: center;
  font-size: 2.5rem;
  color: ${theme.accent};
  margin-bottom: 3rem;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -8px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background: ${theme.accent};
    border-radius: 2px;
  }
`;

const ProjectsGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: 30px;
  
  ${device.tablet} {
    grid-template-columns: repeat(2, 1fr);
  }
  
  ${device.laptop} {
    grid-template-columns: repeat(3, 1fr);
  }
`;

const ProjectCard = styled(motion.div)`
  background: ${theme.lightGray};
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-10px);
  }
`;

const ProjectImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
`;

const ProjectContent = styled.div`
  padding: 1.5rem;

  h3 {
    color: ${theme.accent};
    font-size: 1.5rem;
    margin-bottom: 1rem;
  }

  p {
    color: ${theme.secondary};
    margin-bottom: 1rem;
  }
`;

const ProjectLinks = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;

  a {
    color: ${theme.accent};
    text-decoration: none;
    padding: 0.5rem 1rem;
    border: 2px solid ${theme.accent};
    border-radius: 5px;
    transition: all 0.3s ease;

    &:hover {
      background: ${theme.accent};
      color: white;
    }
  }
`;

const DownloadButton = styled(motion.a)`
  display: inline-block;
  margin: 2rem auto;
  padding: 1rem 2rem;
  background: ${theme.accent};
  color: white;
  text-decoration: none;
  border-radius: 8px;
  font-weight: bold;
  cursor: pointer;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 6px 20px rgba(0, 0, 0, 0.15);
  }
`;

const ButtonContainer = styled.div`
  text-align: center;
  margin-top: 3rem;
`;

const Projects = () => {
  const projects = [
    {
      title: "E-commerce Website",
      description: "A full-stack e-commerce platform built with React, Node.js, and MongoDB. Features include user authentication, product management, shopping cart, and payment integration.",
      image: "/images/ecommerce.jpg",
      demo: "https://your-ecommerce-demo.com",
      github: "https://github.com/yourusername/ecommerce"
    },
    {
      title: "Task Management App",
      description: "A responsive task management application with real-time updates. Built using React, Firebase, and Material-UI. Includes features like task categorization, due dates, and team collaboration.",
      image: "/images/task-app.jpg",
      demo: "https://your-task-app-demo.com",
      github: "https://github.com/yourusername/task-manager"
    },
    {
      title: "Weather Dashboard",
      description: "A weather forecast application that provides real-time weather data using OpenWeatherMap API. Built with React and styled-components, featuring location search and 5-day forecast.",
      image: "/images/weather-app.jpg",
      demo: "https://your-weather-demo.com",
      github: "https://github.com/yourusername/weather-dashboard"
    },
    {
      title: "Portfolio Website",
      description: "A modern portfolio website built with React and styled-components. Features smooth scrolling, responsive design, and animated components using Framer Motion.",
      image: "/images/portfolio.jpg",
      demo: "https://your-portfolio-demo.com",
      github: "https://github.com/yourusername/portfolio"
    }
  ];

  return (
    <ProjectsSection id="projects">
      <Container>
        <Title data-aos="fade-down">My Projects</Title>
        <ButtonContainer>
          <DownloadButton
            href="/files/Gokul_CV.pdf"
            download="Gokul_CV.pdf"
            target="_blank"
            rel="noopener noreferrer"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Download CV
          </DownloadButton>
        </ButtonContainer>
        <ProjectsGrid>
          {projects.map((project, index) => (
            <ProjectCard
              key={index}
              data-aos="fade-up"
              data-aos-delay={index * 100}
            >
              <ProjectImage src={project.image} alt={project.title} />
              <ProjectContent>
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <ProjectLinks>
                  <a href={project.demo} target="_blank" rel="noopener noreferrer">Live Demo</a>
                  <a href={project.github} target="_blank" rel="noopener noreferrer">GitHub</a>
                </ProjectLinks>
              </ProjectContent>
            </ProjectCard>
          ))}
        </ProjectsGrid>
      </Container>
    </ProjectsSection>
  );
};

export default Projects;
